import React from 'react';
import './App.css';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import ListGroup from 'react-bootstrap/ListGroup';
import ListGroupItem from 'react-bootstrap/ListGroupItem';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal'


class Recommender extends React.Component {
    constructor(props) {
        super(props);
        this.subject = React.createRef();
        this.state = {
            allCourses: this.props.aCourses,
            subjects: this.props.sList,
            intList: this.props.iList,
            likedCourses: this.props.rCourses,
            chips: [],
            likedKeys:[]
        };
    }
    render() {
        const [show, setShow] = useState(false);
        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
        return(
            <Card style={{width: 'calc(20vw - 5px)', marginLeft: '5px', height: 'calc(100vh - 10px)', position: 'fixed'}}>
                <Card.Body>
                    <Card.Title>Interest Areas</Card.Title>
                    <Form>
                    <Button variant="primary" onClick={handleShow}>
                        Launch demo modal
                    </Button>
                    <Modal show={show} onHide={handleClose}>
                        <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <ListGroup>{this.getIntListWords()}</ListGroup>
                        </Modal.Body>
                        <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                            Save Changes
                        </Button>
                        </Modal.Footer>
                    </Modal>

                    <Form.Group controlId="formSubject">
                        <Form.Label>Subject</Form.Label>
                        <Form.Control as="select" ref={this.subject} onClick={() => this.setCourses()}>
                        {this.getSubjectOptions()}
                        </Form.Control>
                    </Form.Group>
                    </Form>
                    <ListGroup>{this.getTags()}</ListGroup>
                </Card.Body>
            </Card>
        )
    }

    getIntListWords(){
        var temp = [];
        for(const word of this.state.intList){
            temp.push(
                <ListGroup.Item>{word}</ListGroup.Item>
            )
        }
        return temp;
    }

    getLikedWOrds(){
        var tempLiked = this.state.likedKeys;
        for(const course of this.state.likedCourses){
            for(const eKey of course.keywords){
                if(tempLiked.indexOf(eKey) === -1){
                    tempLiked.push(eKey);
                }
            }
        }
        return tempLiked;
    }

    getTags(){
        var newSearchList = this.state.chips;
        var tags = [];
        for(const tag of newSearchList){
          console.log(tag);
          tags.push(
              <ListGroup.Item>{tag} <Button variant="primary" onClick={()=>this.removeTag(tag)}>X</Button></ListGroup.Item>
          )
        }
        return tags;
    }

    removeTag(rTag){
        var newSearchList = this.state.chips;
        for(const tag of newSearchList){
          if(tag === rTag){
            newSearchList.splice(newSearchList.indexOf(tag), 1);
          }
        }
        this.setState({chips: newSearchList})
        this.props.setCourses(this.searchAndFilter.searchAndFilter(this.props.courses, newSearchList, this.subject.current.value, this.minimumCredits.current.value, this.maximumCredits.current.value, this.state.buttonValue));
    }

    getSubjectOptions() {
        let subjectOptions = [];
        for(const subject of this.state.subjects) {
          subjectOptions.push(<option key={subject}>{subject}</option>);
        }
        return subjectOptions;
    }


}

export default Recommender;
