import React from 'react';
import './App.css';
import Sidebar from './Sidebar';
import CourseArea from './CourseArea';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import CartCourse from './CartCourse';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allCourses: {},
      filteredCourses: {},
      subjects: [],
      cartCourses: {}
    };
  }

  componentDidMount() {
    fetch('https://mysqlcs639.cs.wisc.edu:5000/classes').then(
      res => res.json()
    ).then(data => this.setState({allCourses: data, filteredCourses: data, subjects: this.getSubjects(data)}));
  }

  getSubjects(data) {
    let subjects = [];
    subjects.push("All");

    for(const course of Object.values(data)) {
      if(subjects.indexOf(course.subject) === -1)
        subjects.push(course.subject);
    }

    return subjects;
  }

  setCourses(courses) {
    this.setState({filteredCourses: courses})
  }

  // Start

  setCartCourses(name, section, subsection){
    console.log(name);
    console.log(section);
    var newCart = this.state.cartCourses
    var addedCourse = {};
    if(section === ""){
      for(const course of Object.entries(cartCourses)){
        if(course.name === name){
          return;
        }
      }
      addedCourse.name = name;
      addedCourse.section = "Not Selected";
      addedCourse.subsection = "Not Selected";
    }
    else if(subsection === ""){
      for(const course of Object.entries(cartCourses)){
        if(course.section === section){
          return;
        }
      }
      addedCourse.name = name;
      addedCourse.section = section;
      addedCourse.subsection = "Not found";
    }
    else{
      for(const course of Object.entries(cartCourses)){
        if(course.subsection === subsection){
          return;
        }
      }
      addedCourse.name = name;
      addedCourse.section = section;
      addedCourse.subsection = subsection;
    }
    this.setState({cartCourses: addedCourse})
  }

  // End

  render() {
    return (
      <>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
        />
        <Tabs>
          <Tab eventKey="home" title="Home">
            <Sidebar setCourses={(courses) => this.setCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects}/>
              <div style={{marginLeft: '20vw'}}>
                <CourseArea data={this.state.filteredCourses} setCartCourses={(name, section, subsection)=>this.setCartCourses(name, section, subsection)}/>
              </div>
          </Tab>
          <Tab eventKey="cart" title="Cart">
            <CartCourse data={this.state.cartCourses}/>
          </Tab>
        </Tabs>
      </>
    )
  }
}

export default App;
